float3 drawflare(float2 p, float intensity, float rnd, float speed, int id, float2 res , float blur, float glow)
{
    p.y /= res.x/res.y;
    float flarehueoffset = (1. / 32.) * float(id) * 0.1;
    float lingrad = distance(float2(0.), p); //linear radial gradient
    float expgrad = 1. / exp(lingrad * (fract(rnd) * 0.66 + 0.33)); //exponential radial gradient
    float3 colgrad = hsv2rgb(float3( fract( (expgrad * 8.) + (speed) + flarehueoffset), pow(1.-abs(expgrad*2.-1.), 0.45), 20.0 * expgrad * intensity)); //rainbow spectrum effect


    float blades = length(p * sin(2.0 * atan2(p.y, p.x))); //draw 6 blades

    float comp = pow(1. - saturateF( blades ), 13. - (blur*12.)); // sharpen effect
    comp += saturateF((expgrad-0.9)) * 3.;
    comp = pow(comp * expgrad, 8 + (1.-intensity) * 5.); // compose and sharpen effect
    float3 col = float3(comp) * colgrad/1.5;
    float d = length(p);
    d = smoothstep(intensity/1. * 2.,intensity/10. * 2. , d)/2. * glow;
//    col /= 1.5;
    col += d;
    return  col ; // apply color
}














